        return this.changeUserData(credential.user);
    }
    async googleLogout() {
        await this.auth.signOut();
        return this.router.navigate(['/']);
    }
    changeUserData(user) {
        const data = {
            uid: user.uid,
            email: user.email,
            displayName: user.displayName,
            photoURL: user.photoURL,
